import React, { useState, useEffect } from 'react';
import AddUserForm from './AddUserForm';
import { useNavigate } from 'react-router-dom';
import "./Home.css"

const Home = () => {
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login');
        }
    }, [navigate]);

    const [searchTerm, setSearchTerm] = useState('');
    const [itemsPerPage, setItemsPerPage] = useState(10);
    const [currentPage, setCurrentPage] = useState(1);
    const [showFilterDropdown, setShowFilterDropdown] = useState(false);
    const [showAddUserDropdown, setShowAddUserDropdown] = useState(false);
    const [showAddUserForm, setShowAddUserForm] = useState(false);
    const [filters, setFilters] = useState({
        status: '',
        role: '',
        date: ''
    });

    const handleLogout = () => {
        localStorage.removeItem('token');
        navigate('/login');
    };

    const handleAddUserClick = () => {
        setShowAddUserForm(true);
        setShowAddUserDropdown(false);
    };

    const toggleFilterDropdown = () => {
        setShowFilterDropdown(!showFilterDropdown);
        if (showAddUserDropdown) setShowAddUserDropdown(false);
    };

    const toggleAddUserDropdown = () => {
        setShowAddUserDropdown(!showAddUserDropdown);
        if (showFilterDropdown) setShowFilterDropdown(false);
    };

    const handleFilterChange = (e) => {
        const { name, value } = e.target;
        setFilters(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const applyFilters = () => {
        console.log('Applying filters:', filters);
        // Add your filter logic here
        setShowFilterDropdown(false);
    };

    const resetFilters = () => {
        setFilters({
            status: '',
            role: '',
            date: ''
        });
        setShowFilterDropdown(false);
    };





    return (
        <div className="home-container">
            <header className="header">
                <h1>User Management System</h1>
                <button onClick={handleLogout} className="logout-button">
                    Logout
                </button>
            </header>

            <div className="content-container">

                <div className="controls-container">
                    <div className="search-container">
                        <input
                            type="text"
                            placeholder="Search users..."
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                            className="search-input"
                        />
                    </div>

                    <div className="action-buttons">
                        <div className="dropdown-container">
                            <button 
                                className="filter-button"
                                onClick={toggleFilterDropdown}
                            >
                                <span className="button-icon">üîç</span>
                                Filter
                                <span className="dropdown-arrow">‚ñº</span>
                            </button>
                            {showFilterDropdown && (
                                <div className="dropdown-menu filter-dropdown">
                                    <div className="dropdown-item">
                                        <label>Status</label>
                                        <select 
                                            name="status" 
                                            value={filters.status}
                                            onChange={handleFilterChange}
                                        >
                                            <option value="">All Status</option>
                                            <option value="active">Active</option>
                                            <option value="inactive">Inactive</option>
                                            <option value="pending">Pending</option>
                                        </select>
                                    </div>
                                    <div className="dropdown-item">
                                        <label>Role</label>
                                        <select 
                                            name="role" 
                                            value={filters.role}
                                            onChange={handleFilterChange}
                                        >
                                            <option value="">All Roles</option>
                                            <option value="admin">Admin</option>
                                            <option value="user">User</option>
                                            <option value="editor">Editor</option>
                                        </select>
                                    </div>
                                    <div className="dropdown-item">
                                        <label>Date Range</label>
                                        <input 
                                            type="date" 
                                            name="date" 
                                            value={filters.date}
                                            onChange={handleFilterChange}
                                        />
                                    </div>
                                    <div className="dropdown-actions">
                                        <button 
                                            className="apply-btn"
                                            onClick={applyFilters}
                                        >
                                            Apply
                                        </button>
                                        <button 
                                            className="reset-btn"
                                            onClick={resetFilters}
                                        >
                                            Reset
                                        </button>
                                    </div>
                                </div>
                            )}
                        </div>

                        <div className="dropdown-container">
                            <button 
                                className="add-user-button"
                                onClick={toggleAddUserDropdown}
                            >
                                <span className="button-icon">+</span>
                                Add User
                                <span className="dropdown-arrow">‚ñº</span>
                            </button>
                            {showAddUserDropdown && (
                                <div className="dropdown-menu add-user-dropdown">
                                    <button 
                                        className="dropdown-item"
                                        onClick={handleAddUserClick}
                                    >
                                        <span className="icon">üë§</span>
                                        <span>Add New User</span>
                                    </button>
                                    <button className="dropdown-item">
                                        <span className="icon">üì§</span>
                                        <span>Import Users</span>
                                    </button>
                                    <button className="dropdown-item">
                                        <span className="icon">üìù</span>
                                        <span>Bulk Update</span>
                                    </button>
                                </div>
                            )}

                            {showAddUserForm && (
                                <AddUserForm 
                                    onClose={() => setShowAddUserForm(false)}
                                />
                            )}
                        </div>

                    <div className="pagination-controls">
                    <span>Show:</span>
                    <select
                        value={itemsPerPage}
                        onChange={(e) => {
                            setItemsPerPage(Number(e.target.value));
                            setCurrentPage(1);
                        }}
                        className="page-selector"
                    >
                        <option value={10}>10</option>
                        <option value={25}>25</option>
                        <option value={50}>50</option>
                        <option value={100}>100</option>
                    </select>
                    <span>entries per page</span>
                </div>
                </div>
                </div>



            </div>
        </div>
    );
}

export default Home;
