import React, { useState, useEffect, useRef } from 'react';
import AddUserForm from './AddUserForm';
import FilterUserForm from './FilterUserForm';
import { useNavigate } from 'react-router-dom';
import "./Home.css"

const Home = () => {
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login');
        }
    }, [navigate]);

    const [searchTerm, setSearchTerm] = useState('');
    const [itemsPerPage, setItemsPerPage] = useState(10);
    const [currentPage, setCurrentPage] = useState(1);
    const [showFilterDropdown, setShowFilterDropdown] = useState(false);
    const [showAddUserForm, setShowAddUserForm] = useState(false);
    const [filters, setFilters] = useState({
        firstName: '',
        lastName: '',
        email: ''
    });

    const toggleAddUserForm = () => {
        setShowAddUserForm(prev => !prev);
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        navigate('/login');
    };

    const handleAddUserClick = () => {
        setShowAddUserForm(true);
        setShowAddUserDropdown(false);
    };

    const toggleFilterDropdown = () => {
        setShowFilterDropdown(!showFilterDropdown);
        if (showAddUserDropdown) setShowAddUserDropdown(false);
    };

    const toggleAddUserDropdown = () => {
        setShowAddUserDropdown(!showAddUserDropdown);
        if (showFilterDropdown) setShowFilterDropdown(false);
    };

    const handleFilterChange = (e) => {
        const { name, value } = e.target;
        setFilters(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const applyFilters = () => {
        console.log('Applying filters:', filters);
        // Filter logic will be applied to your users list
        // You can access the filters using the filters state
        setShowFilterDropdown(false);
    };

    const resetFilters = () => {
        setFilters({
            firstName: '',
            lastName: '',
            email: ''
        });
        setShowFilterDropdown(false);
    };





    return (
        <div className="home-container">
            <header className="header">
                <h1>User Management System</h1>
                <button onClick={handleLogout} className="logout-button">
                    Logout
                </button>
            </header>

            <div className="content-container">

                <div className="controls-container">
                    <div className="search-container">
                        <input
                            type="text"
                            placeholder="Search users..."
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                            className="search-input"
                        />
                    </div>

                    <div className="action-buttons">
                        {/* Filter Dropdown */}
                        <div className="dropdown-container">
                            <button 
                                className="filter-button"
                                onClick={toggleFilterDropdown}
                            >
                                <span className="button-icon">🔍</span>
                                Filter
                                <span className="dropdown-arrow">▼</span>
                            </button>
                            {showFilterDropdown && (
                                <div className="dropdown-menu filter-dropdown">
                                    <FilterUserForm 
                                        onClose={toggleFilterDropdown}
                                        onApply={() => {
                                            applyFilters();
                                            setShowFilterDropdown(false);
                                        }}
                                        onClear={resetFilters}
                                        filters={filters}
                                        onFilterChange={handleFilterChange}
                                    />
                                </div>
                            )}
                        </div>

                        {/* Add User Button */}
                        <div className="dropdown-container">
                            <button 
                                className="add-user-button"
                                onClick={toggleAddUserForm}
                            >
                                <span className="button-icon">+</span>
                                Add User
                            </button>
                            {showAddUserForm && (
                                <AddUserForm 
                                    onClose={() => setShowAddUserForm(false)}
                                />
                            )}
                        </div>

                    <div className="pagination-controls">
                    <span>Show:</span>
                    <select
                        value={itemsPerPage}
                        onChange={(e) => {
                            setItemsPerPage(Number(e.target.value));
                            setCurrentPage(1);
                        }}
                        className="page-selector"
                    >
                        <option value={10}>10</option>
                        <option value={25}>25</option>
                        <option value={50}>50</option>
                        <option value={100}>100</option>
                    </select>
                    <span>entries per page</span>
                </div>
                </div>
                </div>



            </div>
        </div>
    );
}

export default Home;
