import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import "./Home.css"

const Home = () => {
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login');
        }
    }, [navigate]);

    const [searchTerm, setSearchTerm] = useState('');
    const [itemsPerPage, setItemsPerPage] = useState(10);
    const [currentPage, setCurrentPage] = useState(1);
    const [showFilterDropdown, setShowFilterDropdown] = useState(false);
    const [showAddUserDropdown, setShowAddUserDropdown] = useState(false);
    const [filters, setFilters] = useState({
        status: '',
        role: '',
        date: ''
    });

    const handleLogout = () => {
        localStorage.removeItem('token');
        navigate('/login');
    };

    const toggleFilterDropdown = () => {
        setShowFilterDropdown(!showFilterDropdown);
        if (showAddUserDropdown) setShowAddUserDropdown(false);
    };

    const toggleAddUserDropdown = () => {
        setShowAddUserDropdown(!showAddUserDropdown);
        if (showFilterDropdown) setShowFilterDropdown(false);
    };

    const handleFilterChange = (e) => {
        const { name, value } = e.target;
        setFilters(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const applyFilters = () => {
        console.log('Applying filters:', filters);
        // Add your filter logic here
        setShowFilterDropdown(false);
    };

    const resetFilters = () => {
        setFilters({
            status: '',
            role: '',
            date: ''
        });
        setShowFilterDropdown(false);
    };


    const handleAddUser = () => {


        return(
            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-40">
            <div className="bg-white rounded-lg p-6 w-full max-w-md mx-4 max-h-[90vh] overflow-y-auto">
              <div className="flex justify-between items-center mb-4">
                <h3 className="text-lg font-semibold">
                  {user ? 'Edit User' : 'Add New User'}
                </h3>
                <button onClick={onClose} className="text-gray-500 hover:text-gray-700">
                  <X size={20} />
                </button>
              </div>

              <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                  <label className="block text-sm font-medium mb-1">Name *</label>
                  <input
                    type="text"
                    className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 ${errors.name ? 'border-red-500' : 'border-gray-300'}`}
                    value={formData.name}
                    onChange={(e) => handleInputChange('name', e.target.value)}
                  />
                  {errors.name && <p className="text-red-500 text-sm mt-1">{errors.name}</p>}
                </div>

                <div>
                  <label className="block text-sm font-medium mb-1">Email *</label>
                  <input
                    type="email"
                    className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 ${errors.email ? 'border-red-500' : 'border-gray-300'}`}
                    value={formData.email}
                    onChange={(e) => handleInputChange('email', e.target.value)}
                  />
                  {errors.email && <p className="text-red-500 text-sm mt-1">{errors.email}</p>}
                </div>

                <div>
                  <label className="block text-sm font-medium mb-1">Phone *</label>
                  <input
                    type="tel"
                    className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 ${errors.phone ? 'border-red-500' : 'border-gray-300'}`}
                    value={formData.phone}
                    onChange={(e) => handleInputChange('phone', e.target.value)}
                  />
                  {errors.phone && <p className="text-red-500 text-sm mt-1">{errors.phone}</p>}
                </div>

                <div>
                  <label className="block text-sm font-medium mb-1">Website</label>
                  <input
                    type="url"
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    value={formData.website}
                    onChange={(e) => handleInputChange('website', e.target.value)}
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium mb-1">Company *</label>
                  <input
                    type="text"
                    className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 ${errors.company ? 'border-red-500' : 'border-gray-300'}`}
                    value={formData.company.name}
                    onChange={(e) => handleInputChange('company.name', e.target.value)}
                  />
                  {errors.company && <p className="text-red-500 text-sm mt-1">{errors.company}</p>}
                </div>

                <div>
                  <label className="block text-sm font-medium mb-1">City</label>
                  <input
                    type="text"
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    value={formData.address.city}
                    onChange={(e) => handleInputChange('address.city', e.target.value)}
                  />
                </div>

                <div className="flex gap-2 mt-6">
                  <button
                    type="submit"
                    disabled={loading}
                    className="flex-1 bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 transition-colors disabled:opacity-50 flex items-center justify-center gap-2"
                  >
                    {loading && <Loader className="animate-spin" size={16} />}
                    {user ? 'Update User' : 'Add User'}
                  </button>
                  <button
                    type="button"
                    onClick={onClose}
                    className="flex-1 bg-gray-500 text-white px-4 py-2 rounded-md hover:bg-gray-600 transition-colors"
                  >
                    Cancel
                  </button>
                </div>
              </form>
            </div>
          </div>
        )
    }



    return (
        <div className="home-container">
            <header className="header">
                <h1>User Management System</h1>
                <button onClick={handleLogout} className="logout-button">
                    Logout
                </button>
            </header>

            <div className="content-container">

                <div className="controls-container">
                    <div className="search-container">
                        <input
                            type="text"
                            placeholder="Search users..."
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                            className="search-input"
                        />
                    </div>

                    <div className="action-buttons">
                        <div className="dropdown-container">
                            <button 
                                className="filter-button"
                                onClick={toggleFilterDropdown}
                            >
                                <span className="button-icon">üîç</span>
                                Filter
                                <span className="dropdown-arrow">‚ñº</span>
                            </button>
                            {showFilterDropdown && (
                                <div className="dropdown-menu filter-dropdown">
                                    <div className="dropdown-item">
                                        <label>Status</label>
                                        <select 
                                            name="status" 
                                            value={filters.status}
                                            onChange={handleFilterChange}
                                        >
                                            <option value="">All Status</option>
                                            <option value="active">Active</option>
                                            <option value="inactive">Inactive</option>
                                            <option value="pending">Pending</option>
                                        </select>
                                    </div>
                                    <div className="dropdown-item">
                                        <label>Role</label>
                                        <select 
                                            name="role" 
                                            value={filters.role}
                                            onChange={handleFilterChange}
                                        >
                                            <option value="">All Roles</option>
                                            <option value="admin">Admin</option>
                                            <option value="user">User</option>
                                            <option value="editor">Editor</option>
                                        </select>
                                    </div>
                                    <div className="dropdown-item">
                                        <label>Date Range</label>
                                        <input 
                                            type="date" 
                                            name="date" 
                                            value={filters.date}
                                            onChange={handleFilterChange}
                                        />
                                    </div>
                                    <div className="dropdown-actions">
                                        <button 
                                            className="apply-btn"
                                            onClick={applyFilters}
                                        >
                                            Apply
                                        </button>
                                        <button 
                                            className="reset-btn"
                                            onClick={resetFilters}
                                        >
                                            Reset
                                        </button>
                                    </div>
                                </div>
                            )}
                        </div>

                        <div className="dropdown-container">
                            <button 
                                className="add-user-button"
                                onClick={toggleAddUserDropdown}
                            >
                                <span className="button-icon">+</span>
                                Add User
                                <span className="dropdown-arrow">‚ñº</span>
                            </button>
                            {showAddUserDropdown && (
                                <div className="dropdown-menu add-user-dropdown">
                                  {handleAddUser}
                                </div>
                            )}
                        </div>

                    <div className="pagination-controls">
                    <span>Show:</span>
                    <select
                        value={itemsPerPage}
                        onChange={(e) => {
                            setItemsPerPage(Number(e.target.value));
                            setCurrentPage(1);
                        }}
                        className="page-selector"
                    >
                        <option value={10}>10</option>
                        <option value={25}>25</option>
                        <option value={50}>50</option>
                        <option value={100}>100</option>
                    </select>
                    <span>entries per page</span>
                </div>
                </div>
                </div>



            </div>
        </div>
    );
}

export default Home;
