import React, { useState, useEffect } from 'react';
import './AddUserForm.css';
import { api, ADD_USER_API } from '../../utils/Constants';

const AddUserForm = ({ user, onClose }) => {
    const [formData, setFormData] = useState({
        firstname: '',
        lastname: '',
        email: '',
        phone: '',
        website: '',
        company: '',  
        city: '',
        department: ''
    });
    const [errors, setErrors] = useState({});
    const [loading, setLoading] = useState(false);

    console.log(formData,"formData");

    // useEffect(() => {
    //     if (user) {
    //         setFormData({
    //             firstname: user.firstname || '',
    //             lastname: user.lastname || '',
    //             email: user.email || '',
    //             phone: user.phone || '',
    //             website: user.website || '',
    //             company: user.company || '',
    //             city: user.city || '',
    //             department: user.department || ''
    //         });
    //     }
    // }, [user]);

    const handleInputChange = (field, value) => {
        if (field.includes('.')) {
            const [parent, child] = field.split('.');
            setFormData(prev => ({
                ...prev,
                [parent]: {
                    ...prev[parent],
                    [child]: value
                }
            }));
        } else {
            setFormData(prev => ({
                ...prev,
                [field]: value
            }));
        }
    };

    const validateForm = () => {
        const newErrors = {};

        // Validate firstname
        if (!formData.firstname.trim()) newErrors.firstname = 'Name is required';

        // Validate email
        if (!formData.email.trim()) {
            newErrors.email = 'Email is required';
        } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
            newErrors.email = 'Email is invalid';
        }

        // Validate phone
        if (!formData.phone.trim()) newErrors.phone = 'Phone is required';

        // Validate company (as string)
        if (!formData.company || !formData.company.trim()) {
            newErrors.company = 'Company name is required';
        }


        if (!formData.city.trim()) newErrors.city = 'City name is required';
        if (!formData.department.trim()) newErrors.department = 'Department name is required';

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleAddUser = async (e) => {
        console.log("add user btn clicked")
        e.preventDefault();
        if (!validateForm()) return;
        console.log(formData);

        setLoading(true);
        try {
            console.log('Form submitted:', formData);
            await new Promise(resolve => setTimeout(resolve, 1000));
           const response = await api.post(ADD_USER_API, {
            firstname: formData.firstname,
            lastname: formData.lastname,
            email: formData.email,
            phone: formData.phone,
            website: formData.website,
            company: formData.company,
            city: formData.city,
            department: formData.department
           });
           console.log(response);


            onClose();
        } catch (error) {
            console.error('Error submitting form:', error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="add-user-modal">
            <div className="modal-content">
                <div className="modal-header">
                    <h3>{user ? 'Edit User' : 'Add New User'}</h3>
                    <button onClick={onClose} className="close-btn">Ã—</button>
                </div>

                <form  className="user-form">
                    <div className="form-group">
                        <label>First Name <span className="required">*</span></label>
                        <input
                            type="text"
                            className={`form-input ${errors.firstname ? 'error' : ''}`}
                            value={formData.firstname}
                            onChange={(e) => handleInputChange('firstname', e.target.value)}
                        />
                        {errors.name && <span className="error-message">{errors.name}</span>}
                    </div>

                    <div className="form-group">
                        <label>Last Name <span className="required">*</span></label>
                        <input
                            type="text"
                            className={`form-input ${errors.lastname ? 'error' : ''}`}
                            value={formData.lastname}
                            onChange={(e) => handleInputChange('lastname', e.target.value)}
                        />
                        {errors.lastname && <span className="error-message">{errors.lastname}</span>}
                    </div>

                    <div className="form-group">
                        <label>Email <span className="required">*</span></label>
                        <input
                            type="email"
                            className={`form-input ${errors.email ? 'error' : ''}`}
                            value={formData.email}
                            onChange={(e) => handleInputChange('email', e.target.value)}
                        />
                        {errors.email && <span className="error-message">{errors.email}</span>}
                    </div>

                    <div className="form-group">
                        <label>Phone <span className="required">*</span></label>
                        <input
                            type="tel"
                            className={`form-input ${errors.phone ? 'error' : ''}`}
                            value={formData.phone}
                            onChange={(e) => handleInputChange('phone', e.target.value)}
                        />
                        {errors.phone && <span className="error-message">{errors.phone}</span>}
                    </div>

                    <div className="form-group">
                        <label>Website</label>
                        <input
                            type="url"
                            className="form-input"
                            value={formData.website}
                            onChange={(e) => handleInputChange('website', e.target.value)}
                        />
                    </div>

                    <div className="form-group">
                        <label>Company <span className="required">*</span></label>
                        <input
                            type="text"
                            className={`form-input ${errors.company ? 'error' : ''}`}
                            value={formData.company.name}
                            onChange={(e) => handleInputChange('company.name', e.target.value)}
                        />
                        {errors.company && <span className="error-message">{errors.company}</span>}
                    </div>

                    <div className="form-group">
                        <label>City</label>
                        <input
                            type="text"
                            className={`form-input ${errors.city ? 'error' : ''}`}
                            value={formData.city}
                            onChange={(e) => handleInputChange('city', e.target.value)}
                        />
                    </div>
                    <div className="form-group">
                        <label>Department</label>
                        <input
                            type="text"
                            className={`form-input ${errors.department ? 'error' : ''}`}
                            value={formData.department}
                            onChange={(e) => handleInputChange('department', e.target.value)}
                        />
                        {errors.department && <span className="error-message">{errors.department}</span>}
                    </div>

                    <div className="form-actions">
                        <button
                            type="submit"
                            className="btn primary"
                            disabled={loading} onClick={handleAddUser}
                        >
                            {/* {loading ? 'Processing...' : (user ? 'Update User' : 'Add User')} */}
                            Add User
                        </button>
                        <button
                            type="button"
                            className="btn secondary"
                            onClick={onClose}
                            disabled={loading}
                        >
                            Cancel
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default AddUserForm;
